import 'dart:math';import 'package:flutter/material.dart';import 'package:teez_project/data/model/card_model.dart';class CatalogViewModel extends ChangeNotifier {  CatalogViewModel() {    loadMoreProducts();  }  final List<CardModel> _products = [];  bool _isLoading = false;  List<CardModel> get products => _products;  bool get isLoading => _isLoading;  void loadMoreProducts() {    if (_isLoading) return;    _isLoading = true;    notifyListeners();    Future.delayed(const Duration(seconds: 1), () {      List<CardModel> newProducts = List.generate(10, (index) {        int id = _products.length + index+1;        return CardModel(            name: 'Product $id',            description: 'Description for product $id',            price: Random().nextInt(1000),            image: 'https://resources.cdn-kaspi.kz/img/m/p/h32/h70/84378448199710.jpg?format=gallery-large');      });      _products.addAll(newProducts);      _isLoading = false;      notifyListeners();    });  }  onNotification(ScrollNotification scrollInfo) {    if (!isLoading &&        scrollInfo.metrics.pixels + 600 >=            scrollInfo.metrics.maxScrollExtent) {      loadMoreProducts();    }  }}