import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:teez_project/presentation/catalog/viewModel/viewModel.dart';import 'package:teez_project/presentation/catalog/widgets/product_card_body.dart';import 'package:shimmer/shimmer.dart';class CatalogBody extends StatelessWidget {  const CatalogBody({super.key});  @override  Widget build(BuildContext context) {    final readVm = context.read<CatalogViewModel>();    final watchVm = context.watch<CatalogViewModel>();    return Scaffold(      appBar: AppBar(        title: const Text('Teez'),        backgroundColor: Colors.green,      ),      body: NotificationListener<ScrollNotification>(        onNotification: (ScrollNotification scrollInfo) {          readVm.onNotification(scrollInfo);          return false;        },        child: GridView.builder(          gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(            crossAxisCount: 2,            childAspectRatio: 0.65,            crossAxisSpacing: 10,            mainAxisSpacing: 10,          ),          padding: const EdgeInsets.all(10),          itemCount: readVm.products.length + (readVm.isLoading ? 2 : 0),          itemBuilder: (context, index) {            if (index >= readVm.products.length && readVm.isLoading) {              return Shimmer.fromColors(                baseColor: Colors.grey[300]!,                highlightColor: Colors.white,                child: Container(                  width: MediaQuery.of(context).size.width / 2 - 20,                  height: (MediaQuery.of(context).size.width / 2 - 20) / 0.65, // Use the same aspect ratio                  decoration: BoxDecoration(                    color: Colors.grey[300],                    borderRadius: BorderRadius.circular(10),                  ),                ),              );            } else if (index < readVm.products.length) {              final card = readVm.products[index];              return ProductCard(card: card);            } else {              return const SizedBox.shrink();            }          },        ),      ),    );  }}