import 'package:flutter/material.dart';import 'package:teez_project/data/model/card_model.dart';class ProductCard extends StatelessWidget {  final CardModel card;  const ProductCard({super.key, required this.card});  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(10),        border: Border.all(color: Colors.black12)      ),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          ClipRRect(            borderRadius: const BorderRadius.vertical(top: Radius.circular(10)),            child: Image.network(              card.image ?? "",              height: 150,              width: double.infinity,              fit: BoxFit.contain,            ),          ),          Padding(            padding: const EdgeInsets.all(8.0),            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Text(                  card.name ?? "Name",                  style: const TextStyle(                    fontSize: 16,                    fontWeight: FontWeight.bold,                  ),                ),                const SizedBox(height: 4),                Text(                  card.description ?? "Description",                  style: TextStyle(                    fontSize: 14,                    color: Colors.grey[600],                  ),                  maxLines: 2,                  overflow: TextOverflow.ellipsis,                ),                const SizedBox(height: 8),                Text(                  '\$${card.price ?? "0"}',                  style: const TextStyle(                    fontSize: 16,                    fontWeight: FontWeight.bold,                    color: Colors.teal,                  ),                ),              ],            ),          ),        ],      ),    );  }}